import { React, useState} from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './RegisterPage.css';

export const RegisterPage = () => {
  const [username, setUsername] = useState("");
  const [major, setMajor] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confPass, setConfPass] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  const url = "http://localhost:3005";

  const handleRegister = async (e) => {
    // handle errors/validate input
    if (confPass != password) {
      setErrorMessage("Passwords must match");
    }
    else {
      e.preventDefault();
      const response = await fetch(`${url}/login/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ username: username, major: major, email: email, password: password, confPass })
      });

      const data = await response.json();
      if (data.success) {
        navigate("/home");
      }
      else {
        // error
      }
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleRegister}>
        <h1>Register Page</h1>
        <div className="input-box">
          <input type="text" placeholder='Username' required />
        </div>
        <div className="input-box">
          <input type="text" placeholder='Major' required />
        </div>
        <div className="input-box">
          <input type="email" placeholder='Email' required />
        </div>
        <div className="input-box">
          <input type="password" placeholder='Password' required />
        </div>
        <div className="input-box">
          <input type="password" placeholder='Confirm Password' required />
        </div>

        <button type="submit">Register</button>

        <div className="Login-link">
          <p>Already have an account? <a href="/">Login Here</a></p>
        </div>
      </form>
    </div>
  );
};

export default RegisterPage;
